<?php if (!isset($_SERVER['HTTP_REFERER']) || parse_url($_SERVER['HTTP_REFERER'], PHP_URL_HOST) != $_SERVER['SERVER_NAME']) {	header('HTTP/1.0 404');	exit;}require 'urlat_in_connector.class.php';try {	$response = null;		$user = isset($_REQUEST['user'])?$_REQUEST['user']:null;	$pass = isset($_REQUEST['pass'])?$_REQUEST['pass']:null;        	if ($user != null)            $response = urlat_in_connector::send( array('url'=>$_REQUEST['url']), $user, $pass );	else             $response = urlat_in_connector::send( array('url'=>$_REQUEST['url']) );	echo '{"status":"ok", "shortly":"'.urlat_in_connector::getShortUrl($response).'"}';}catch (Exception $e) {	echo '{"status":"error"}';}exit;